}

function getUserContext(inputContent) {
  let userContext = "default";

  try {
    if (!/^-?\d+$/.test(inputContent)) {
      userContext = JSON.parse(inputContent);
    }
  } finally {
    return userContext;
  }
}

function run(now) {
    var gmtBrazil = new Date(now);

    var day = String(gmtBrazil.getDate()).padStart(2, '0');
    var month = String(gmtBrazil.getMonth() + 1).padStart(2, '0');
    var year = gmtBrazil.getFullYear();

    var hours = String(gmtBrazil.getHours()).padStart(2, '0');
    var minutes = String(gmtBrazil.getMinutes()).padStart(2, '0');

    var formattedDate = `${day}/${month}/${year} - ${hours}:${minutes} - ${hours}`;

    return formattedDate;
}

