function run(contextMessage) {
    try {
        contextMessage = JSON.parse(contextMessage);

        return {
            messageType: "ExceptionContextMessage",
            serviceOrigin: contextMessage.serviceOrigin || "[COBR] Principal",
            blockOrigin: contextMessage.blockOrigin
        };
    } catch(e) {
        return {
            serviceOrigin: "[COBR] Principal",
            blockOrigin: "Error inside exception skill",
            redirectTo: "Início"
        };
    }
}

const run = (exceptionMessage) => {
    let contextMessage = {
        serviceOrigin: "", 
        blockOrigin: "",
        inputType: "",
        input: "",
        lastBlockException: ""
    };

    try {
        exceptionMessage = JSON.parse(exceptionMessage);

        contextMessage = exceptionMessage;

        // Se resposta do usuário não existe, adiciona flag de "Inatividade".
        if (!contextMessage.input) {
            contextMessage.input = 'Inatividade';
            return contextMessage;
        }

        let parsedInput = JSON.parse(contextMessage.input);

        let isNumber = typeof parsedInput === 'number';
        if (isNumber) {
            parsedInput = String(parsedInput);
        }

        contextMessage.input = parsedInput;
        contextMessage.inputType = isNumber ? 'text/plain' : parsedInput.type;
    } catch(e) {
        contextMessage.input = exceptionMessage.input || 'Error';
    }

    return contextMessage;
}

