function getProcessedInput(contextMessage) {
    contextMessage = JSON.parse(contextMessage);

    let options = [
        {
            regex: /^(((https?:\/\/)[^\s.]+|(www))\.[\w][^\s]+)$/,
            value: 'Somente link'
        },
        {
            regex: /^((\u00a9|\u00ae|[\u2000-\u3300]|\ud83c[\ud000-\udfff]|\ud83d[\ud000-\udfff]|\ud83e[\ud000-\udfff]|\s)+)$/,
            value: 'Somente emojis'
        }
    ];

    let props = {
        input: contextMessage.input,
        inputType: contextMessage.inputType,
        options
    };

    let config = {
        isNumberMenu: false,
        isReversed: false,
        shouldRemoveSpecialCharacters: true,
        shouldRemoveWhiteSpaces: true
    };

    processedInput = validateInputOptions(props, config)
    return processedInput;
}

