function run(smallTalksResponse) {
    const UNEXPECTED = "Input inesperado";

    try 
    {
        smallTalksResponse = JSON.parse(smallTalksResponse);

        const intentions = [ 
            "Compliment", 
            "Frustration", 
            "Greeting", 
            "GoodBye", 
            "Thanks",
            "GoodMorning",
            "GoodNight",
            "GoodAfternoon",
            "Confirmation",
            "Negation" 
        ];

        const { cursed, matches } = smallTalksResponse.analysis;

        let smallTalk = "";

        if (matches.length > 0) {
            const intention = matches[0].smallTalk;

            if (intentions.indexOf(intention) > -1) {
                smallTalk = intention;
            } 
        }

        return {
            intention: smallTalk,
            hasBadWord: cursed
        }
    }
    catch(e)
    {
        return UNEXPECTED;
    }

    return UNEXPECTED;
}

function run(statePreviousName) {

    let destination = {
        service: '[COBR] Principal',
        stateDestination: "Início"
    }

    try
    {
        switch (statePreviousName) {
            case "Atendimento Humano":
                destination.service = '[AtlasX] ATH';
                break;
        }

        return destination
    }
    catch (e) 
    {
        return destination
    }
}

function run(destination, blockBeforeRedirect) {
    return getRedirectMessage(destination, blockBeforeRedirect);
}

function getRedirectMessage(destination, blockBeforeRedirect) {
    const { service, stateDestination } = JSON.parse(destination);

    const redirectMessage = {
        messageType: 'SkillRedirectMessage',
        destination: service,
        redirectTo: stateDestination,
        blockOrigin: blockBeforeRedirect,
        serviceOrigin: "exceção"
    }

    return redirectMessage;
}

function run(contextMessage, lastMenu) {
    // Se variável não existe
    if (!lastMenu) {
        return false;
    }

    const context = JSON.parse(contextMessage);

    return context.blockOrigin === lastMenu;
}

